  describe "the view produced by a GET request on page 1 of the Movie DB discover endpoint" do
    it "produces 20 results for the first page" do
      expect(popular_shows[:results].count).to eq 20
    end

    it "checks that all the popular tv show names render on the page" do
      tv_show_names = popular_shows[:results].map { |x| x[:name] }
      popular_shows[:results].each_with_index do |x, i|
        expect(popular_shows[:results][i]).to have_content "#{tv_show_names[i]}"
      end
    end

